/**
 * Static SVG Icons — remplace @iconify/react pour les pages critiques (homepage).
 * Élimine les requêtes réseau vers api.iconify.design et permet le SSR.
 */

import type { SVGProps } from 'react';

interface IconProps extends SVGProps<SVGSVGElement> {
  size?: number;
}

function createIcon(inner: string, displayName: string, viewBox = '0 0 24 24') {
  const Component = ({ size = 24, width, height, className, ...props }: IconProps) => {
    const w = width ?? size;
    const h = height ?? size;
    return (
      <svg
        xmlns="http://www.w3.org/2000/svg"
        viewBox={viewBox}
        width={w}
        height={h}
        className={className}
        {...props}
        dangerouslySetInnerHTML={{ __html: inner }}
      />
    );
  };
  Component.displayName = displayName;
  return Component;
}

